import "./styles.css";

const Buttons = ({ handleCompare, onBeautify, handleClear, handleBase64, action, setAction}) => {

  return (
    <div className="button-group">
      <button className="compare-button" onClick={handleCompare}>
        Compare
      </button>
      <button className="Beautify-button" onClick={onBeautify}>
        Beautify
      </button>

      <div>
        <select value={action} onChange={(e) => setAction(e.target.value)} onClick={(action) => handleBase64}>
          <option value="encode">Encode</option>
          <option value="decode">Decode</option>
        </select>
      </div>

      <button className="clear-button" onClick={handleClear}>
        Clear
      </button>
    </div>
  );
};

export default Buttons;
