import React, { useState } from "react";
import "./styles.css";

const BaseButtons = ({firstContent, secondContent, handleBase64}) => {
    const [content, setContent] = useState(""); // State for the content to be encoded/decoded
    const [action, setAction] = useState("encode"); // Default action

    const handleClick = (content, action) => {
      handleBase64(content, action); // Call the handleBase64 function passed as a prop
    };

  return (
    <div className="base-button-group">

    <div className="base-button-subgroup">
      <button className="base-button" onClick={() => handleBase64(firstContent, "1", "decode")}>
        Decode
      </button>
      <button className="base-button" onClick={() => handleBase64(firstContent, "1", "encode")}>
        Encode
      </button>
      </div>
      <div className="base-button-subgroup">
      <button className="base-button" onClick={() => handleBase64(secondContent, "2", "decode")}>
        Decode
      </button>
      <button className="base-button" onClick={() => handleBase64(secondContent, "2", "encode")}>
        Encode
      </button>
      </div>
    </div>
  );
};

export default BaseButtons;
