  const handleResize = (e) => {
    e.target.style.height = "auto"; // Reset height to auto to shrink if needed
    e.target.style.height = `${e.target.scrollHeight}px`; // Set height to scrollHeight
  };

  return (
    <div className="container">
      <div className="input-group">
        <div className="input-container">
          <label htmlFor="first-content">First Content</label>
          <textarea
            id="first-content"
            value={firstContent}
            onChange={(e) => {
              setFirstContent(e.target.value);
              handleResize(e);
            }}
            placeholder="Enter first content"
            style={{ overflow: "hidden" }}
          />
        </div>
        <div className="input-container">
          <label htmlFor="second-content">Second Content</label>
          <textarea
            id="second-content"
            value={secondContent}
            onChange={(e) => {
              setSecondContent(e.target.value);
              handleResize(e);
            }}
            placeholder="Enter second content"
            style={{ overflow: "hidden" }}
          />
        </div>
      </div>
    </div>
  );
}

export default inputContent;
