import React from 'react';

const XmlRenderer = ({ xmlString }) => {
  // Function to highlight <Highlight> tags
  const highlightXml = (xml) => {
    return xml
      .replace(/<Highlight>(.*?)<\/Highlight>/g, (match, p1) => {
        return `<span style="background-color: yellow; font-weight: bold;">${p1}</span>`;
      });
  };

  const highlightedXml = highlightXml(xmlString);

  return (
    <div>
      <div
        dangerouslySetInnerHTML={{ __html: highlightedXml }}
        style={{ whiteSpace: 'pre-wrap', fontFamily: 'monospace' }}
      />
    </div>
  );
};

export default XmlRenderer;
