import "./styles.css";

function inputContent({firstContent, secondContent, setFirstContent, setSecondContent}) {


  // Function to auto-resize textarea
  const handleResize = (e) => {
    e.target.style.height = "auto"; // Reset height to auto to shrink if needed
    e.target.style.height = `${e.target.scrollHeight}px`; // Set height to scrollHeight
  };

  const handleFileUpload = (e, order) => {
    const file = e.target.files[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();

      if(order ==="1"){
      reader.onload = (event) => {
        setFirstContent(event.target.result); 
      };
    } else {
      reader.onload = (event) => {
        setSecondContent(event.target.result); 
      };
    }
      reader.readAsText(file);
    } else {
      alert("Please upload a valid .txt file");
    }
  };

  return (
    <div className="container">
      <div className="input-group">
        <div className="input-container">
          <label className="input-text">Input First Content</label>
          <input type="file" accept=".txt,.xml,.json" onChange={(e) => handleFileUpload(e, "1")} className="input-text"></input>
          <textarea
            id="first-content"
            value={firstContent}
            onChange={(e) => {
              setFirstContent(e.target.value);
              handleResize(e);
            }}
            placeholder="Enter first content"
          />
        </div>
        <div className="input-container">
          <label className="input-text">Input Second Content</label>
          <input type="file"  accept=".txt,.xml,.json" onChange={(e) => handleFileUpload(e, "2")} className="input-text"></input>
          <textarea
            id="second-content"
            value={secondContent}
            onChange={(e) => {
              setSecondContent(e.target.value);
              handleResize(e);
            }}
            placeholder="Enter second content"
          />
        </div>
      </div>
    </div>
  );
}

export default inputContent;
