import React, { useState } from 'react';

const HighlightedXMLDisplay = ({ xmlString }) => {
  const [textContent, setTextContent] = useState(xmlString);

  // Convert <Highlight> tags to <span> for highlighting
  const highlightedXml = textContent.replace(
    /<Highlight>(.*?)<\/Highlight>/g,
    (match, p1) => `<span style="background-color: yellow; font-weight: bold;">${p1}</span>`
  );

  return (
    <div style={{ position: 'relative', marginBottom: '20px' }}>
      {/* Display highlighted text */}
      <div
        style={{
          whiteSpace: 'pre-wrap',
          fontFamily: 'monospace',
          border: '1px solid #ccc',
          padding: '10px',
          minHeight: '100px', // Set a minimum height
          overflowY: 'auto'
        }}
        dangerouslySetInnerHTML={{ __html: highlightedXml }}
      />

      {/* Hidden textarea for text input or copying */}
      <textarea
        style={{ display: 'none' }} // Hide the textarea
        value={textContent}
        onChange={(e) => setTextContent(e.target.value)}
      />
    </div>
  );
};


export default HighlightedXMLDisplay;
