import React from 'react';

const HighlightedXMLDisplay = ({ xmlString }) => {
  // Function to highlight <Highlight> tags while preserving XML structure
  const highlightXml = (xml) => {
    // Replace <Highlight> with <span> for highlighting
    const highlightedXml = xml.replace(
      /<Highlight>(.*?)<\/Highlight>/g,
      (match, p1) => `<span style="background-color: yellow; font-weight: bold;">${p1}</span>`
    );

    // Escape all other angle brackets in the highlighted XML
    return highlightedXml.replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/&lt;span/g, '<span').replace(/&gt;<\/span>/g, '</span>');
  };

  const highlightedXml = highlightXml(xmlString);

  return (
    <div>
      <div
        dangerouslySetInnerHTML={{ __html: highlightedXml }}
      />
      <textarea
        value={xmlString}
        readOnly
        rows={15}
        style={{ width: '100%', fontFamily: 'monospace' }}
      />
    </div>
  );
};

export default HighlightedXMLDisplay;
