import "./styles.css";
import React, { useState, useRef} from "react";
import InputContent from "./components/InputContent";
import Button from "./components/Button";
import OutputResult from "./components/OutputResult";
import logo from "./constants/edicom-logo.png";

export default function App() {
  const [firstContent, setFirstContent] = useState("");
  const [secondContent, setSecondContent] = useState("");
  const [fisrtFileName, setFirstFileName] = useState(""); 
  const [secondFileName, setSecondFileName] = useState("");
  const [output, setOutput] = useState("");
  const firstFileInputRef = useRef(null);
  const secondFileInputRef = useRef(null);

  const handleCompare = () => {
    if (firstContent === secondContent) {
      setOutput("The contents are identical.");
    } else {
      compareContents("/diffFormatter/compare");
    }
  };

  const handleClear = () => {
    setFirstContent("");
    setSecondContent("");
    setFirstFileName("");
    setSecondFileName("");
    setOutput("");
    if (firstFileInputRef.current) {
      firstFileInputRef.current.value = ""; // Clear first file input
    }
    if (secondFileInputRef.current) {
      secondFileInputRef.current.value = ""; // Clear second file input
    }
  };

const compareContents = async () => {
  try{
    if (firstContent === "" || secondContent === ""){
      setOutput("Contents cannot be emtpy.");
      return;
    }
  const response = await fetch(
    "http://localhost:8080/diffFormatter/compare",
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        firstContent: firstContent,
        secondContent: secondContent,
      }),
    }
  );
  if (response.ok) {
    const data = await response.text(); 
    setOutput(data);
  } else {
    setOutput("Error occurred while fetching the response.");
  }
} catch (error) {
  setOutput(`Fetch error: ${error.message}`);
}
};

const onBeautify = async () => {
  try {
    if (firstContent === ""){
      setOutput("Fisrt content cannot be emtpy.");
    }
  const response = await fetch(
    "http://localhost:8080/diffFormatter/beautify",
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: firstContent
    }
  );
  if (response.ok) {
    const data = await response.text(); 
    setOutput(data);
  } else {
    setOutput("Error occurred while fetching the response.");
  }
} catch (error) {
  setOutput(`Fetch error: ${error.message}`);
}
};

  return (
    <div className="App">
    <header className="header">
      <div className="logo-container">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      <div className="title-container">
        <h1 className="title">XML & JSON Comparison Tool</h1>
      </div>
    </header>
      <InputContent firstContent={firstContent} secondContent={secondContent} firstFileInputRef={firstFileInputRef} secondFileInputRef={secondFileInputRef} setFirstContent={setFirstContent} 
      setSecondContent={setSecondContent} setFirstFileName={setFirstFileName} setSecondFileName={setSecondFileName}/>
      <Button handleClear={handleClear} onBeautify={onBeautify} handleCompare={handleCompare} />
      <OutputResult output={output}/>
    </div>
  );
}
